mapper.update:
update bbs
set ansnum = ansnum + 1
where grpno = #{grpno} and ansnum > #{ansnum}

mapper.select:
SELECT bbsno, title, grpno, indent, ansnum
FROM bbs
WHERE bbsno = #{bbsno}

mapper.update:
update bbs
wname = #{wname} , title = #{title} , content = #{content},
filename= #{filename}, filesize= #{filesize}
where bbsno = #{bbsno}

mapper.select:
select count(bbsno) as cnt
from bbs
where bbsno = #{bbsno}
and passwd = #{passwd}

mapper.update:
UPDATE bbs
SET viewcnt = viewcnt + 1
WHERE bbsno = #{bbsno}

mapper.select:
SELECT bbsno, wname, title, content, viewcnt, wdate,filename
FROM bbs
WHERE bbsno = #{bbsno}
